    static func tileForZone(_ zone: Int) -> CGImage? {
        switch (zone - 1) % 6 {
        case 0: return charredTile
        case 1: return lavaRockTile
        case 2: return brimstoneTile
        case 3: return fleshWallTile
        case 4: return boneTile
        case 5: return hellfireObsidianTile
        default: return charredTile
        }
    }

    static func capForZone(_ zone: Int) -> CGImage? {
        switch (zone - 1) % 6 {
        case 0: return charredCap
        case 1: return lavaCap
        case 2: return brimstoneCap
        case 3: return fleshCap
        case 4: return boneCap
        case 5: return hellfireCap
        default: return charredCap
        }
    }
